import { PluginOptions, Plugin } from '@assetpack/core';

interface MipmapOptions<T extends string = ''> extends PluginOptions<'fix' | T> {
    /** A template for denoting the resolution of the images. */
    template?: string;
    /** An object containing the resolutions that the images will be resized to. */
    resolutions?: {
        [x: string]: number;
    };
    /** A resolution used if the fixed tag is applied. Resolution must match one found in resolutions. */
    fixedResolution?: string;
}
declare function mipmap(options?: Partial<MipmapOptions>): Plugin<MipmapOptions>;

type SpineOptions = MipmapOptions<'spine'>;
declare function spineAtlasMipmap(options?: Partial<SpineOptions>): Plugin<SpineOptions>;

export { MipmapOptions, SpineOptions, mipmap, spineAtlasMipmap };
